    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String auxCodigo = edtCodigo.getText();
        String auxNome = edtNome.getText();
        String auxIdade = edtIdade.getText();

        if (validaCamposVazios(auxCodigo)) {
            int codigo = Integer.parseInt(auxCodigo);

            ModelPrimeiro primeiro = new ModelPrimeiro(
                    codigo,
                    auxNome,
                    auxIdade);

            ModelPrimeiro primeiroBanco = primeiroDB.getPrimeiro(codigo);

            if (primeiroBanco.getCodigo() > 0) {
                if (primeiroDB.gravarAlteracao(primeiro)) {
                    JOptionPane.showMessageDialog(null, "Registro alterado com sucesso!");

                    limpaCampos();

                    habilitaCampos(true);

                } else {
                    JOptionPane.showMessageDialog(null, "Erro ao gravar alteração!");
                    habilitaCampos(false);
                    edtCodigo.requestFocus();
                }
            } else {
                if (primeiroDB.gravarInsercao(primeiro)) {
                    JOptionPane.showMessageDialog(null, "Registro inserido com sucesso!");

                    limpaCampos();

                    habilitaCampos(false);

                    edtCodigo.requestFocus();
                } else {
                    JOptionPane.showMessageDialog(null, "Erro ao inserir registro!");
                }
            }
        } else {
            JOptionPane.showMessageDialog(null, "Existem campos nao preenchidos!");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

