    private boolean validaCamposVazios(String auxCodigo) {
        if (auxCodigo.equals("")) {
            JOptionPane.showMessageDialog(null, "Preencha o codigo!!");
        } else {
            int codigo = Integer.parseInt(auxCodigo);
            if (codigo > 0) {
                return true;
            } else {
                JOptionPane.showMessageDialog(null, "Preencha o codigo!!");
            }
        }
        return true;
    }

    private void limpaCampos() {
        edtCodigo.setText("");
        edtNome.setText("");
        edtIdade.setText("");
    }

    private void habilitaCampos(boolean habilita) {
        edtCodigo.setEnabled(habilita);
        edtNome.setEnabled(habilita);
        edtIdade.setEnabled(habilita);
    }
